import React, { useState, useEffect } from "react";
import "./App.css";

const quizData = {
  categories: [
    {
      id: "js_basics",
      name: "JavaScript Basics",
      questions: [
        {
          id: "q1",
          question:
            "What is the correct syntax for referring to an external script called 'script.js'?",
          options: [
            "A. <script name='script.js'>",
            "B. <script href='script.js'>",
            "C. <script src='script.js'>",
            "D. <script file='script.js'>",
          ],
          correctAnswer: "C",
          timeLimit: 10,
        },
        {
          id: "q2",
          question: "Which company developed JavaScript?",
          options: ["A. Microsoft", "B. Netscape", "C. Google", "D. Mozilla"],
          correctAnswer: "B",
          timeLimit: 10,
        },
      ],
    },
    // Additional categories can be added here
  ],
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(10);
  const [showResults, setShowResults] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [username, setUsername] = useState("");
  const [selectedTopic, setSelectedTopic] = useState("");

  useEffect(() => {
    if (isStarted && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(interval);
    } else if (isStarted && timer === 0) {
      handleNext();
    }
  }, [timer, isStarted]);

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex((prev) => prev + 1);
      setTimer(10);
    } else {
      setShowResults(true);
    }
  };

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentIndex]?.correctAnswer) {
      setScore((prev) => prev + 1);
    }
    handleNext();
  };

  const startQuiz = () => {
    if (username.trim() && selectedTopic) {
      const selectedCategory = quizData.categories.find(
        (category) => category.id === selectedTopic
      );
      setQuestions(selectedCategory?.questions || []);
      setIsStarted(true);
    } else {
      alert("Please enter your name and select a topic to continue.");
    }
  };

  if (!isStarted) {
    return (
      <div className="start-screen">
        <h1>Welcome to the Quiz App</h1>
        <p>Test your knowledge and see how much you can score!</p>
        <input
          type="text"
          placeholder="Enter your full name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="username-input"
        />
        <p>Please select a topic to continue:</p>
        <div className="topic-selection">
          {quizData.categories.map((category) => (
            <button
              key={category.id}
              className={`topic-button ${
                selectedTopic === category.id ? "selected" : ""
              }`}
              onClick={() => setSelectedTopic(category.id)}
            >
              {category.name}
            </button>
          ))}
        </div>
        <button onClick={startQuiz} className="start-button">
          Start Quiz
        </button>
      </div>
    );
  }

  return (
    <div className="quiz-app">
      {showResults ? (
        <div className="results">
          <h2>Quiz Results</h2>
          <p>
            You scored {score} out of {questions.length} (
            {((score / questions.length) * 100).toFixed(2)}%)
          </p>
          <p>
            {score / questions.length >= 0.8
              ? "Great job!"
              : score / questions.length >= 0.6
              ? "Well done!"
              : "Keep practicing!"}
          </p>
        </div>
      ) : (
        <div className="question-container">
          <h2>{questions[currentIndex]?.question}</h2>
          <div className="options">
            {questions[currentIndex]?.options.map((option, idx) => (
              <button
                key={idx}
                className="option-button"
                onClick={() => handleAnswer(option)}
              >
                {option}
              </button>
            ))}
          </div>
          <div className="controls">
            <p>Time remaining: {timer}s</p>
            <button onClick={handleNext} className="next-button">
              Next
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
