import React from "react";

const StartScreen = ({
  username,
  setUsername,
  selectedTopic,
  setSelectedTopic,
  startQuiz,
  categories,
}) => {
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-6">
          <div className="start-screen">
            <h1 className="heading">
              Welcome to{" "}
              <span>
                QUIZ<strong>Mania</strong>
              </span>
            </h1>
            <div className="">
              <div className="quiz-rules">
                Please read all the rules about this quiz before you start.
              </div>
              <div className="form-control border-0 p-0">
                <label htmlFor="username">Full Name</label>
                <input
                  type="text"
                  id="username"
                  placeholder="Enter your full name"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="username-input form-control"
                />
              </div>
              <div className="topic-selection mt-4">
                <label>Select a Topic:</label>
                {categories.map((category) => (
                  <div key={category.id} className="form-check">
                    <input
                      type="radio"
                      id={category.id}
                      name="topic"
                      value={category.id}
                      className="form-check-input"
                      checked={selectedTopic === category.id}
                      onChange={() => setSelectedTopic(category.id)}
                    />
                    <label htmlFor={category.id} className="form-check-label">
                      {category.name}
                    </label>
                  </div>
                ))}
              </div>
              <button
                onClick={startQuiz}
                className="start-button btn btn-primary mt-3"
                disabled={!username || !selectedTopic}
              >
                Start Quiz
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StartScreen;
