import React, { useState, useEffect } from "react";
import "./App.css";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(10);
  const [showResults, setShowResults] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  useEffect(() => {
    // Fetch or initialize questions
    const fetchQuestions = async () => {
      const quizQuestions = [
        {
          question: "What is React?",
          options: ["Library", "Framework", "Language", "None"],
          correctAnswer: "Library",
        },
        {
          question:
            "Which hook is used to manage state in a functional component?",
          options: ["useEffect", "useState", "useReducer", "useContext"],
          correctAnswer: "useState",
        },
        {
          question: "What is JSX?",
          options: [
            "JavaScript XML",
            "JSON XML",
            "Java Syntax Extension",
            "None",
          ],
          correctAnswer: "JavaScript XML",
        },
      ];
      setQuestions(quizQuestions);
    };

    fetchQuestions();
  }, []);

  useEffect(() => {
    if (isStarted && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(interval);
    } else if (isStarted && timer === 0) {
      handleNext();
    }
  }, [timer, isStarted]);

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex((prev) => prev + 1);
      setTimer(10);
    } else {
      setShowResults(true);
    }
  };

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentIndex]?.correctAnswer) {
      setScore((prev) => prev + 1);
    }
    handleNext();
  };

  const startQuiz = () => {
    setIsStarted(true);
  };

  if (!questions || questions.length === 0) {
    return <div>Loading questions...</div>;
  }

  if (!isStarted) {
    return (
      <div className="start-screen">
        <h1>Welcome to the Quiz App</h1>
        <p>Test your knowledge and see how much you can score!</p>
        <button onClick={startQuiz} className="start-button">
          Start Quiz
        </button>
      </div>
    );
  }

  return (
    <div className="quiz-app">
      {showResults ? (
        <div className="results">
          <h2>Quiz Results</h2>
          <p>
            You scored {score} out of {questions.length} (
            {((score / questions.length) * 100).toFixed(2)}%)
          </p>
          <p>
            {score / questions.length >= 0.8
              ? "Great job!"
              : score / questions.length >= 0.6
              ? "Well done!"
              : "Keep practicing!"}
          </p>
        </div>
      ) : (
        <div className="question-container">
          <h2>{questions[currentIndex]?.question}</h2>
          <div className="options">
            {questions[currentIndex]?.options.map((option, idx) => (
              <button
                key={idx}
                className="option-button"
                onClick={() => handleAnswer(option)}
              >
                {option}
              </button>
            ))}
          </div>
          <div className="controls">
            <p>Time remaining: {timer}s</p>
            <button onClick={handleNext} className="next-button">
              Next
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
