import React, { useState, useEffect } from "react";

const categories = [
  "React Basics",
  "JavaScript Basics",
  "Angular Basics",
  "Flutter",
];
const questions = {
  "React Basics": [
    {
      question: "What is React?",
      options: [
        "A JavaScript library for building user interfaces",
        "A CSS framework",
        "A programming language",
        "None of the above",
      ],
      correct: 0,
    },
    {
      question: "What is JSX?",
      options: [
        "A syntax extension for JavaScript",
        "A type of React component",
        "A new programming language",
        "None of the above",
      ],
      correct: 0,
    },
  ],
};

function App() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(10);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [quizOver, setQuizOver] = useState(false);

  const startQuiz = () => {
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimer(10);
    setSelectedAnswer(null);
    setQuizOver(false);
  };

  useEffect(() => {
    if (selectedCategory && !quizOver) {
      if (timer > 0) {
        const interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
        return () => clearInterval(interval);
      } else {
        nextQuestion();
      }
    }
  }, [timer, selectedCategory, quizOver]);

  const handleAnswer = (index) => {
    setSelectedAnswer(index);
    if (index === questions[selectedCategory][currentQuestionIndex].correct) {
      setScore((prev) => prev + 1);
    }
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < questions[selectedCategory].length - 1) {
      setCurrentQuestionIndex((prev) => prev + 1);
      setTimer(10);
      setSelectedAnswer(null);
    } else {
      setQuizOver(true);
    }
  };

  const renderCategorySelection = () => (
    <div className="category-selection">
      <h1>Welcome to QuizMania</h1>
      <p>Please select a category to start the quiz:</p>
      {categories.map((category) => (
        <button
          key={category}
          onClick={() => {
            setSelectedCategory(category);
            startQuiz();
          }}
        >
          {category}
        </button>
      ))}
    </div>
  );

  const renderQuestion = () => {
    const question = questions[selectedCategory][currentQuestionIndex];
    return (
      <div className="quiz">
        <h2>{question.question}</h2>
        <div className="options">
          {question.options.map((option, index) => (
            <button
              key={index}
              className={
                selectedAnswer === index
                  ? index === question.correct
                    ? "correct"
                    : "incorrect"
                  : ""
              }
              onClick={() => handleAnswer(index)}
              disabled={selectedAnswer !== null}
            >
              {option}
            </button>
          ))}
        </div>
        <div className="controls">
          <p>Time Remaining: {timer} seconds</p>
          <button onClick={nextQuestion} disabled={selectedAnswer === null}>
            Next
          </button>
        </div>
      </div>
    );
  };

  const renderResults = () => (
    <div className="results">
      <h1>Quiz Over!</h1>
      <p>
        Your Score: {score} / {questions[selectedCategory].length}
      </p>
      <p>
        {score / questions[selectedCategory].length > 0.8
          ? "Great job!"
          : score / questions[selectedCategory].length > 0.6
          ? "Well done!"
          : "Keep practicing!"}
      </p>
      <button onClick={() => setSelectedCategory(null)}>
        Back to Categories
      </button>
    </div>
  );

  return (
    <div className="quiz-app">
      {!selectedCategory && renderCategorySelection()}
      {selectedCategory && !quizOver && renderQuestion()}
      {quizOver && renderResults()}
    </div>
  );
}

export default App;
